`,paraId:62,tocIndex:7},{value:"db.js",paraId:63,tocIndex:7},{value:`/**
 * \u6253\u5F00\u6570\u636E\u5E93
 * @param {object} dbName \u6570\u636E\u5E93\u7684\u540D\u5B57
 * @param {string} storeName \u4ED3\u5E93\u540D\u79F0
 * @param {string} version \u6570\u636E\u5E93\u7684\u7248\u672C
 * @return {object} \u8BE5\u51FD\u6570\u4F1A\u8FD4\u56DE\u4E00\u4E2A\u6570\u636E\u5E93\u5B9E\u4F8B
 */
function openDB(dbName, version = 1) {
    return new Promise((resolve, reject) => {
        var db; // \u5B58\u50A8\u521B\u5EFA\u7684\u6570\u636E\u5E93
        // \u6253\u5F00\u6570\u636E\u5E93\uFF0C\u82E5\u6CA1\u6709\u5219\u4F1A\u521B\u5EFA
        const request = indexedDB.open(dbName, version);

        // \u6570\u636E\u5E93\u6253\u5F00\u6210\u529F\u56DE\u8C03
        request.onsuccess = function (event) {
            db = event.target.result; // \u5B58\u50A8\u6570\u636E\u5E93\u5BF9\u8C61
            console.log("\u6570\u636E\u5E93\u6253\u5F00\u6210\u529F");
            resolve(db);
        };

        // \u6570\u636E\u5E93\u6253\u5F00\u5931\u8D25\u7684\u56DE\u8C03
        request.onerror = function (event) {
            console.log("\u6570\u636E\u5E93\u6253\u5F00\u62A5\u9519");
        };

        // \u6570\u636E\u5E93\u6709\u66F4\u65B0\u65F6\u5019\u7684\u56DE\u8C03
        request.onupgradeneeded = function (event) {
            // \u6570\u636E\u5E93\u521B\u5EFA\u6216\u5347\u7EA7\u7684\u65F6\u5019\u4F1A\u89E6\u53D1
            console.log("onupgradeneeded");
            db = event.target.result; // \u5B58\u50A8\u6570\u636E\u5E93\u5BF9\u8C61
            var objectStore;
            // \u521B\u5EFA\u5B58\u50A8\u5E93
            objectStore = db.createObjectStore("stu", {
                keyPath: "stuId", // \u8FD9\u662F\u4E3B\u952E
                autoIncrement: true // \u5B9E\u73B0\u81EA\u589E
            });
            // \u521B\u5EFA\u7D22\u5F15\uFF0C\u5728\u540E\u9762\u67E5\u8BE2\u6570\u636E\u7684\u65F6\u5019\u53EF\u4EE5\u6839\u636E\u7D22\u5F15\u67E5
            objectStore.createIndex("stuId", "stuId", { unique: true });
            objectStore.createIndex("stuName", "stuName", { unique: false });
            objectStore.createIndex("stuAge", "stuAge", { unique: false });
        };
    });
}

/**
 * \u65B0\u589E\u6570\u636E
 * @param {object} db \u6570\u636E\u5E93\u5B9E\u4F8B
 * @param {string} storeName \u4ED3\u5E93\u540D\u79F0
 * @param {string} data \u6570\u636E
 */
function addData(db, storeName, data) {
    var request = db
        .transaction([storeName], "readwrite") // \u4E8B\u52A1\u5BF9\u8C61 \u6307\u5B9A\u8868\u683C\u540D\u79F0\u548C\u64CD\u4F5C\u6A21\u5F0F\uFF08"\u53EA\u8BFB"\u6216"\u8BFB\u5199"\uFF09
        .objectStore(storeName) // \u4ED3\u5E93\u5BF9\u8C61
        .add(data);

    request.onsuccess = function (event) {
        console.log("\u6570\u636E\u5199\u5165\u6210\u529F");
    };

    request.onerror = function (event) {
        console.log("\u6570\u636E\u5199\u5165\u5931\u8D25");
    };
}

/**
 * \u901A\u8FC7\u4E3B\u952E\u8BFB\u53D6\u6570\u636E
 * @param {object} db \u6570\u636E\u5E93\u5B9E\u4F8B
 * @param {string} storeName \u4ED3\u5E93\u540D\u79F0
 * @param {string} key \u4E3B\u952E\u503C
 */
function getDataByKey(db, storeName, key) {
    return new Promise((resolve, reject) => {
        var transaction = db.transaction([storeName]); // \u4E8B\u52A1
        var objectStore = transaction.objectStore(storeName); // \u4ED3\u5E93\u5BF9\u8C61
        var request = objectStore.getAll(); // \u901A\u8FC7\u4E3B\u952E\u83B7\u53D6\u6570\u636E

        request.onerror = function (event) {
            console.log("\u4E8B\u52A1\u5931\u8D25");
        };

        request.onsuccess = function (event) {
            // console.log("\u4E3B\u952E\u67E5\u8BE2\u7ED3\u679C: ", request.result);
            resolve(request.result);
        };
    });
}
