class TrafficFeeCalculator {
  public goToAirport(way: TravelWay, distance: number): number {
    let trafficFee = 0;
    switch (way) {
      case "bus":
        if (distance < 10) trafficFee = 4;
        else trafficFee = 6;
        break;
      case "didi":
        if (distance < 3) trafficFee = 8;
        else trafficFee = 8 + (distance - 3) * 3;
        break;
      case "sharedBicycle":
        trafficFee = 2;
        break;
      default:
        break;
    }
    return trafficFee;
  }
}

