        const snakeHead = new SnakeHead(x, y, squareWidth, squareWidth); // \u5B9E\u4F8B\u5316 SnakeHead \u5BF9\u8C61
        this.init(snakeHead, color, collideType.die); // \u8BBE\u7F6E\u8BE5 DOM \u5143\u7D20\u7684 CSS \u4FE1\u606F

