type Order = {
  lineItems: LineItem[];
  country: string;
};

function getOrderTotal(order: Order): number {
  let total = 0;

  // \u8BA1\u7B97\u8BA2\u5355\u9879\u603B\u4EF7
  order.lineItems.forEach((item) => {
    total += item.price * item.quantity;
  });

  // \u5E94\u7528\u7A0E\u7387
  const taxRate = getTaxRate(order.country);
  total += total * taxRate;

  return total;
}

// \u5355\u72EC\u7684\u51FD\u6570\u7528\u4E8E\u83B7\u53D6\u7A0E\u7387
function getTaxRate(country: string): number {
  if (country === "US") {
    return 0.07; // \u7F8E\u56FD\u8425\u4E1A\u7A0E
  } else if (country === "EU") {
    return 0.2; // \u6B27\u6D32\u589E\u503C\u7A0E
  } else {
    return 0; // \u5176\u4ED6\u56FD\u5BB6\u65E0\u7A0E
  }
}
