`,paraId:9,tocIndex:0},{value:"Record<K,  T> \u662F TS \u91CC\u9762\u7684\u4E00\u4E2A\u9AD8\u7EA7\u7C7B\u578B\uFF0C\u901A\u5E38\u8868\u793A\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u5BF9\u8C61\u7684\u952E\u662F K \u7C7B\u578B\uFF0C\u5BF9\u8C61\u7684\u503C\u4E3A T \u7C7B\u578B\u3002",paraId:10,tocIndex:0},{value:"\u6BD4\u5982\u5728\u4E0A\u9762\u7684\u4EE3\u7801\u4E2D\uFF0C\u8868\u793A\u5BF9\u8C61\u7684\u952E\u662F string \u7C7B\u578B\uFF0C\u5BF9\u8C61\u7684\u503C\u53EF\u4EE5\u662F\u4EFB\u610F\u7C7B\u578B\u3002",paraId:11,tocIndex:0},{value:"\u63A5\u4E0B\u6765\u5C31\u662F\u5B9E\u73B0\u89C2\u5BDF\u8005\u6A21\u5F0F\uFF0C\u8FD9\u91CC\u5B58\u5728\u4E24\u4E2A\u89D2\u8272\uFF1A",paraId:12,tocIndex:0},{value:"\u89C2\u5BDF\u8005",paraId:13,tocIndex:0},{value:"\u53D1\u5E03\u8005",paraId:13,tocIndex:0},{value:"\u53D1\u5E03\u8005",paraId:14,tocIndex:0},{value:"\u53D1\u5E03\u8005\u7684\u4F5C\u7528\u662F\uFF1A",paraId:15,tocIndex:0},{value:"\u53EF\u4EE5\u6DFB\u52A0\u89C2\u5BDF\u8005",paraId:16,tocIndex:0},{value:"\u80FD\u591F\u901A\u77E5\u89C2\u5BDF\u8005",paraId:16,tocIndex:0},{value:`// \u53D1\u5E03\u8005
import Watcher from "./Watcher.js";
export default class Dep {
  // \u8BE5\u9759\u6001\u5C5E\u6027\u7528\u4E8E\u6682\u65F6\u4FDD\u5B58\u5F53\u524D\u7684 Watcher \u5BF9\u8C61\uFF0C\u4E3B\u8981\u7528\u4E8E\u8FDB\u884C\u4F9D\u8D56\u7684\u6536\u96C6
  static target: Watcher | null = null;
  // \u7EF4\u62A4\u4E00\u4E2A\u89C2\u5BDF\u8005\u7684\u5217\u8868
  subs: Watcher[];

  constructor() {
    this.subs = [];
  }

  // \u6DFB\u52A0\u89C2\u5BDF\u8005\u5230\u5217\u8868\u91CC\u9762
  addSub(sub: Watcher): void {
    this.subs.push(sub);
  }

  // \u901A\u77E5\u6240\u6709\u89C2\u5BDF\u8005\u66F4\u65B0
  notify(): void {
    this.subs.forEach((sub) => {
      sub.update();
    });
  }
}
